$(document).ready(function() {
	
	$(window).bind('load', function() {
		$('.blog-item').addClass('animated fadeInUp');
	});
	
	if ($(window).width() <= 450) {
		$('.nav-banner').addClass('animated fadeInDown');
	}
	
	/*** Back to Works ***/
	$(window).scroll(function() {
		if ($(window).scrollTop() <= 100) {
			$('.about-me').removeClass('top');
			if ($(window).width() <= 450) {
				$('.nav-banner').addClass('animated fadeInDown');
			}
			else {
				$('.nav-banner').removeClass('animated fadeInDown');
				$('.nav-banner').addClass('animated fadeOutUp');
			}
		}
		
		else {
			if ($(window).width() <= 450) {
				$('.nav-banner').addClass('animated fadeInDown');
			}
			else {
				$('.about-me').addClass('top');
				$('.nav-banner').removeClass('animated fadeOutUp');
				$('.nav-banner').addClass('animated fadeInDown');
			}
		}
	});
	
	$('.image-container').height($('.image-container').width() * 0.56);
	
	/*** Logo and menu effects handlers ***/
	logoHandler();
	menuHandler();	
	toggleConnectOverlay();
});